def compute_reward(obs, terminated, truncated, info):
    temp = info['temp']
    action = info['action']

    temp_diff = abs(temp - 55.0)
    temp_penalty = temp_diff ** 2  # Quadratic penalty for temperature deviation

    energy_penalty = action ** 2  # Quadratic penalty for energy consumption

    reward = -temp_penalty - 0.1 * energy_penalty  # Combine penalties

    if terminated:
        if temp < 20 or temp > 95:
            reward -= 100  # Large negative reward for failure

    return float(reward)
